// RemoveVariations relationships from objects passed in.
// Removes related items from R.Variations (uses pointer comparison, removal does not keep order)
// Sets related.R.Copyright.
func (o *Copyright) RemoveVariations(exec boil.Executor, related ...*Variation) error {
	var err error
	for _, rel := range related {
		rel.CopyrightID.Valid = false
		if rel.R != nil {
			rel.R.Copyright = nil
		}
		if err = rel.Update(exec, "copyright_id"); err != nil {
			return err
		}
	}
	if o.R == nil {
		return nil
	}

	for _, rel := range related {
		for i, ri := range o.R.Variations {
			if rel != ri {
				continue
			}

			ln := len(o.R.Variations)
			if ln > 1 && i < ln-1 {
				o.R.Variations[i] = o.R.Variations[ln-1]
			}
			o.R.Variations = o.R.Variations[:ln-1]
			break
		}
	}

	return nil
}

